import { Title } from "../styled";
import {
  BIO,
  List,
  StyledRenopwacja,
  AnimatedList,
  AnimatedItem,
  FBox,
  Felga,
} from "./styled";
import f from "../../../images/felga.png";

import g1 from "../../../images/Felgi/1.JPG";
import g2 from "../../../images/Felgi/2.JPG";
import g3 from "../../../images/Felgi/3.JPG";
import g4 from "../../../images/Felgi/4.JPG";
import g5 from "../../../images/Felgi/5.JPG";
import g6 from "../../../images/Felgi/6.JPG";
import g7 from "../../../images/Felgi/7.JPG";
import g8 from "../../../images/Felgi/8.JPG";
import g9 from "../../../images/Felgi/9.JPG";
import g10 from "../../../images/Felgi/10.JPG";
import g13 from "../../../images/Felgi/13.JPG";
import g14 from "../../../images/Felgi/14.JPG";

import Gallery from "react-photo-gallery";
import Carousel, { Modal, ModalGateway } from "react-images";
import { useState } from "react";

export const Renowacja = () => {
  const items = [
    "piaskowanie strumieniowo-ścierne,",
    "malowanie proszkowe,",
    "prostowanie na maszynach CNC,",
    "spawanie pęknięć,",
    "odtwarzanie oryginalnego kształtu felgi.",
  ];

  const photos = [
    { src: g1, width: 4, height: 3 },
    { src: g2, width: 1, height: 1 },
    { src: g3, width: 4, height: 3 },
    { src: g4, width: 4, height: 3 },
    { src: g5, width: 4, height: 3 },

    { src: g6, width: 1.2, height: 1 },
    { src: g7, width: 1.2, height: 1 },
    { src: g8, width: 1, height: 1 },
    { src: g9, width: 1, height: 1 },
    { src: g10, width: 1, height: 1 },
    { src: g13, width: 1, height: 1 },
    { src: g14, width: 1, height: 1 },
  ];

  const [current, setCurrent] = useState(0);
  const [viewerIsOpen, setViewerIsOpen] = useState(false);

  const openLightbox = (event, { photo, index }) => {
    setCurrent(index);
    setViewerIsOpen(true);
  };

  const closeLightbox = () => {
    setCurrent(0);
    setViewerIsOpen(false);
  };

  return (
    <StyledRenopwacja>
      <Title>
        Renowacja Felg w Żyrardowie i okolicach – Piaskowanie, Malowanie
        Proszkowe, Naprawa i Odtwarzanie Zniszczonych Felg
      </Title>

      <BIO>
        O felgach wiemy wszystko... Specjalizujemy się w kompleksowej renowacji
        felg – realizujemy zlecenia z <strong>całej Polski</strong>, ale
        działamy przede wszystkim lokalnie w{" "}
        <strong>Żyrardowie i okolicach</strong>. Przywracamy nawet bardzo
        zniszczone felgi do fabrycznego wyglądu i pełnej sprawności.
      </BIO>

      <List>
        <b>Wykonujemy:</b>
        <AnimatedList>
          {items.map((text, index) => (
            <AnimatedItem key={index} delay={index * 2}>
              {text}
            </AnimatedItem>
          ))}
        </AnimatedList>
        Każdy etap naprawy kontrolujemy pod kątem bezpieczeństwa i jakości.
        Zapewniamy szybkie terminy, atrakcyjne ceny i fachowe doradztwo.
      </List>
      <FBox>
        <Felga src={f} alt="" />
      </FBox>

      <Gallery photos={photos} onClick={openLightbox} />
      <ModalGateway>
        {viewerIsOpen ? (
          <Modal onClose={closeLightbox}>
            <Carousel currentIndex={current} views={photos} />
          </Modal>
        ) : null}
      </ModalGateway>
    </StyledRenopwacja>
  );
};
