import { Title } from "../styled";
import {
  BIO,
  List,
  StyledRenopwacja,
  AnimatedList,
  AnimatedItem,
  FBox,
  Felga,
} from "./styled";
import f from "../../../images/felga.png";

import Gallery from 'react-photo-gallery';
import Carousel, { Modal, ModalGateway } from 'react-images';
import { useState } from "react";

export const Renowacja = () => {
  const items = [
    "piaskowanie strumieniowo-ścierne,",
    "malowanie proszkowe,",
    "prostowanie na maszynach CNC,",
    "spawanie pęknięć,",
    "odtwarzanie oryginalnego kształtu felgi.",
  ];

  const photos = [
  { src: '/img1.jpg', width: 4, height: 3 },
  { src: '/img2.jpg', width: 1, height: 1 },
];

  const [current, setCurrent] = useState(0);
  const [viewerIsOpen, setViewerIsOpen] = useState(false);

  const openLightbox = (event, { photo, index }) => {
    setCurrent(index);
    setViewerIsOpen(true);
  };

  const closeLightbox = () => {
    setCurrent(0);
    setViewerIsOpen(false);
  };

  return (
    <StyledRenopwacja>
      <Title>
        Renowacja Felg w Żyrardowie i okolicach – Piaskowanie, Malowanie
        Proszkowe, Naprawa i Odtwarzanie Zniszczonych Felg
      </Title>

      <BIO>
        O felgach wiemy wszystko...
        Specjalizujemy się w kompleksowej renowacji felg – realizujemy zlecenia
        z <strong>całej Polski</strong>, ale działamy przede wszystkim lokalnie
        w <strong>Żyrardowie i okolicach</strong>. Przywracamy nawet bardzo
        zniszczone felgi do fabrycznego wyglądu i pełnej sprawności.
      </BIO>

      <List>
        <b>Wykonujemy:</b>
        <AnimatedList>
          {items.map((text, index) => (
            <AnimatedItem key={index} delay={index * 2}>
              {text}
            </AnimatedItem>
          ))}
        </AnimatedList>
        Każdy etap naprawy kontrolujemy pod kątem bezpieczeństwa i jakości.
        Zapewniamy szybkie terminy, atrakcyjne ceny i fachowe doradztwo.
      </List>
      <FBox>
        <Felga src={f} alt="" />
      </FBox>

        <Gallery photos={photos} onClick={openLightbox} />
      <ModalGateway>
        {viewerIsOpen ? (
          <Modal onClose={closeLightbox}>
            <Carousel currentIndex={current} views={photos} />
          </Modal>
        ) : null}
      </ModalGateway>

    </StyledRenopwacja>
  );
};
