import styled, { keyframes } from "styled-components";

export const StyledSection = styled.section`
width: 90%;
margin-left: 5%;
margin-top: 30px;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
`;


export const BIO = styled.div`
width: 100%;
font-size: 1.2em;
`;

export const List = styled.div`
margin: 0;
margin-top: 50px;
margin-bottom: 140px;
padding: 0;
width: 100%;
font-size: 1.2em;
`;

const pulse = keyframes`
  0% {
    font-weight: 400;
    transform: scale(1);
  }
  10% {

    transform: scale(1.05);
  }
  20% {
    font-weight: 400;
    transform: scale(1);
  }
  100% {
        font-weight: 400;
    transform: scale(1);
  }
`;

export const AnimatedList = styled.ul`
  padding: 0;
  margin: 20px;

`;

export const AnimatedItem = styled.li`
  margin-bottom: 8px;
  transition: 500ms;
  animation: ${pulse} 10s ease infinite;
  animation-delay: ${props => props.delay}s;
`;

const rotate = keyframes`
  0% {
   transform: rotate(0deg);
  }
  100% {

    transform: rotate(360deg);
  }
`;

export const FBox = styled.div`
width: 100%;
position: relative;
`

export const Felga = styled.img`
width: 400px;
height: 400px;
position: absolute;
z-index: -1;
top:0;
right: -300px;
margin-left: 200px;
animation: ${rotate} 10s ease infinite;
`

export const GalleryBox = styled.div`
width: 90%;
align-self: flex-start;
`

export const Title = styled.h2`
width: 90%;
align-self: flex-start;
font-size: 2.2em;

  font-family: 'Roboto Condensed', sans-serif;
  background: linear-gradient(135deg, #bbb 0%, #444 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  text-shadow: 1px 1px 2px #222;
`
