import styled, { keyframes } from "styled-components";

export const StyledRenopwacja = styled.section`
width: 90%;
margin-left: 5%;
margin-top: 30px;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
`;


export const BIO = styled.div`
width: 100%;
font-size: 1.2em;
`;

export const List = styled.div`
margin: 0;
margin-top: 50px;
padding: 0;
width: 100%;
font-size: 1.2em;
`;

const pulse = keyframes`
  0% {
    font-weight: 400;
    transform: scale(1);
  }
  10% {

    transform: scale(1.05);
  }
  20% {
    font-weight: 400;
    transform: scale(1);
  }
  100% {
        font-weight: 400;
    transform: scale(1);
  }
`;

export const AnimatedList = styled.ul`
  padding: 0;
  margin: 20px;

`;

export const AnimatedItem = styled.li`
  margin-bottom: 8px;
  transition: 500ms;
  animation: ${pulse} 10s ease infinite;
  animation-delay: ${props => props.delay}s;
`;

export const Felga = styled.img`
width: 400px;
height: 400px;
position: absolute;
z-index: -1;
top:40%;
right: -200px;
margin-left: 200px;
`
