import styled, { keyframes } from "styled-components";


export const Wrapper = styled.section`
  width: 100%;
  display: flex;
  position: relative;
  min-height: 200px;
  background-image: ${({ bg }) => `url(${bg})`};
  z-index: 0;
  padding-bottom:50px;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(0, 0, 0, 0.5); // <-- stopień przyciemnienia
    z-index: 1;
  }

  > * {
    position: relative;
    z-index: 2; // <-- żeby dzieci były nad przyciemnieniem
  }
`;

export const Logo = styled.img`
aspect-ratio: 1 / 1;
width: 100px;
height: 100px;
filter: brightness(1.5);
margin-left: 50px;
`

const scroll = keyframes`
  0% {
    transform: translateX(100%);
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateX(-100%);
    opacity: 0;
  }
`;

const scroll2 = keyframes`
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
    50% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateX(-100%);
    opacity: 0;
  }
`;

export const BannerWrapper = styled.div`
  margin-top: 100px;
  position: relative;
  width: 100%;
  overflow: hidden;
  white-space: nowrap;
  background: transparent;

  mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
  -webkit-mask-image: linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%);
`;

export const ScrollingText = styled.h1`
user-select: none;
filter: brightness(3);
  position: absolute;
  top: 0;
  left: 0;
  font-size: 2rem;
  color: white;
  white-space: nowrap;
  animation: ${scroll} 40s linear infinite;
  animation-delay: 0s;
  z-index: 999;

  text-shadow:
    0 0 5px crimson,
    0 0 10px crimson,
    0 0 20px crimson,
    0 0 40px rgba(220, 20, 20, 0.6);
`;

export const ScrollingText2 = styled.h2`
user-select: none;
  position: absolute;
  top: 0;
  left: 0;
    opacity: 0;
  font-size: 2rem;
  color: white;
  white-space: nowrap;
  animation: ${scroll2} 40s linear infinite;
  animation-delay: 20s;
  z-index: 999;

  text-shadow:
    0 0 5px crimson,
    0 0 10px crimson,
    0 0 20px crimson,
    0 0 40px rgba(220, 20, 20, 0.6);
`;

export const TilesNav = styled.nav`
padding-top: 100px;
width: 100%;
max-width: 100%;
display: grid;
grid-template-columns: 30% 30% 30%;
gap: 3%;
align-content: center;
justify-content: center;
`

export const SingleTile = styled.img`
width: 100%;
transition: 500ms;
:hover{
    filter: contrast(100);
}
`
